mapper.insert:
insert  /*增加用户信息*/
into sf_user_info (
     id
    ,mobile
    ,password
    ,nickname
    ,gender
    ,birthday
    ,email
    ,avatar
    ,intro
    ,reg_dt
    ,upd_dt
    ,last_dt
  )
values (
     #{id}
    ,#{mobile}
    ,#{password}
    ,#{nickname}
    ,#{gender}
    ,#{birthday}
    ,#{email}
    ,#{avatar}
    ,#{intro}
    ,SYSDATE()
    ,SYSDATE()
    ,SYSDATE()
  )

mapper.delete:
delete /*根据id删除用户信息*/
    sf_user_info
  where id = #{id}
